import React, { useEffect, useState } from 'react'
import moment from 'moment-timezone';

const Weathercard = ({ tempInfo }) => {
    const [weatherState, setWeatherState] = useState("")
    const { temp,
        humidity,
        weathermood,
        pressure,
        name,
        speed,
        country,
        sunset } = tempInfo

    useEffect(() => {
        if (weathermood) {
            switch (weathermood) {
                case 'Clouds': setWeatherState('wi-day-cloudy')
                    break;
                case 'Haze': setWeatherState('wi-fog')
                    break;
                case 'Clear': setWeatherState('wi-day-sunny')
                    break;
                case 'Mist': setWeatherState('wi-dust')
                    break;
                case 'Smoke': setWeatherState('wi-smoke')
                    break;
                case 'Thunderstorm': setWeatherState('wi-rain-wind')
                    break;
                case 'Sprinkle': setWeatherState('wi-sprinkle')
                    break;
                case 'Rain': setWeatherState('wi-rain')
                    break;
                case 'Stars': setWeatherState('wi-stars')
                    break;

                default: setWeatherState('wi-day-sunny')
                    break;
            }
        }
    }, [weathermood])

    //converting the seconds into time 
    let sec = sunset
    let date = new Date(sec * 1000)
    let timeStr = `${date.getHours()}:${date.getMinutes()}`

    // Get the country-wise date string with timezone
  const timezone = moment.tz.guess();
  const countryDateOptions = {
    timeZone: timezone,
    timeZoneName: 'short',
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  };
  const countryDateStr = date.toLocaleString('en-US', countryDateOptions);

    // Get the country-wise date string
    // const countryDateOptions = { timeZone: 'UTC', timeZoneName: 'short', weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    // const countryDateStr = date.toLocaleString('en-US', countryDateOptions);

    return (
        <> {/*our temp card*/}
            <article className='widget'>

                <div className="weatherIcon">
                    <i className={`wi ${weatherState}`}></i>
                </div>

                <div className="weatherInfo">
                    <div className="temperature">
                        <span>{temp}&deg;</span>
                    </div>
                    <div className="description">
                        <div className="weatherCondition">{weathermood}</div>
                        <div className="place">{name},{country}</div>
                    </div>
                </div>


                <div className="date">{countryDateStr}</div>


                {/*our 4 section column*/}
                <div className="extra-temp">
                    <div className="temp-info-minmax">
                        <div className="two-sided-section">
                            <p><i className='wi wi-sunset'></i></p>
                            <p className='extra-info-leftside'>{timeStr}<br />Sunset</p>
                        </div>

                        <div className="two-sided-section">
                            <p><i className='wi wi-humidity'></i></p>
                            <p className='extra-info-leftside'>{humidity}<br />Humidity</p>
                        </div>
                    </div>

                    <div className="weather-extra-info">
                        <div className="two-sided-section">
                            <p><i className='wi wi-rain'></i></p>
                            <p className='extra-info-leftside'>Pressure<br />{pressure}</p>
                        </div>

                        <div className="two-sided-section">
                            <p><i className='wi wi-strong-wind'></i></p>
                            <p className='extra-info-leftside'>Speed<br />{speed}</p>
                        </div>
                    </div>
                </div>
            </article>
        </>
    )
}

export default Weathercard
